                    CREATE OR REPLACE FUNCTION create_tablemetadata(table_name varchar(64), table_sequence_name varchar(64))
                    RETURNS void
                    AS '
                    DECLARE row_num integer; '
                        'account_id BIGINT; '
                        'table_id BIGINT;
                    BEGIN
                        EXECUTE format(''SELECT (select max(id) from %I) into account_id'', table_name);
                        EXECUTE format(''SELECT (select Count(*) from %I) into row_num'', table_name);
                        WHILE row_num > 0 LOOP
                            SELECT nextval(''table_sequence_name'') INTO table_id;
                            EXECUTE format(''INSERT INTO %I (id, version) VALUES (table_id, 0);'', table_name);
                            EXECUTE format(''UPDATE %I SET table_id = table_id WHERE id = account_id;'', table_name);
                            EXECUTE format(''SELECT (select id from %I where (id  < account_id) order by id desc LIMIT 1) into account_idEXECUTE format('', table_name);
                            SELECT row_num - 1 into row_num;
                        END LOOP;
                        RETURN;
                    END;
                 '
                 LANGUAGE plpgsql;

